mapper.select:
SELECT d.device_id AS deviceId, d.device_code AS deviceCode, d.device_name AS deviceName, d.device_type AS deviceType, d.address, d.latitude, d.longitude, d.status FROM devices d LEFT JOIN districts dst ON d.district_id = dst.district_id WHERE d.latitude IS NOT NULL AND d.longitude IS NOT NULL
AND d.status = #{status}
AND dst.district_name = #{districtName}
