mapper.insert:
INSERT INTO devices (
 device_code, device_name, device_type, district_id, address,
 latitude, longitude, rtsp_url, status
)
VALUES (
        #{deviceCode}, #{deviceName}, #{deviceType}, #{districtId}, #{address},
        #{latitude}, #{longitude}, #{rtspUrl}, #{status}
       )

mapper.update:
UPDATE devices
device_name = #{deviceName}, device_type = #{deviceType}, district_id = #{districtId}, address = #{address}, latitude = #{latitude}, longitude = #{longitude}, rtsp_url = #{rtspUrl}, status = #{status},
WHERE device_id = #{deviceId}
